function confirmDisconnect() {
	var rec = new GlideRecord('x_341321_nexmo_dir_nexmo_config');
			  rec.addQuery('internal_name', "main_and_only");
			  rec.query();
			  while (rec.next()) {
				  rec.status= false;
				  rec.api_key = "";
				  rec.api_secret = "";
				  rec.application_id = "";
				  rec.update();
			  }
action.setRedirectURL(current);
