sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	var sm = new sn_ws.RESTMessageV2('x_341321_nexmo_dir.nexmogetph', 'Default GET');
	var endpoint = sm.getEndpoint();
	var gr = new GlideRecord ('x_341321_nexmo_dir_nexmo_config');
	gr.orderByDesc('Created');
	gr.setLimit(1);
	gr.query();
	if (gr.next()){
		var requestone = new sn_ws.RESTMessageV2();
		requestone.setEndpoint(endpoint+'?api_key='+gr.api_key+'&api_secret='+gr.api_secret.getDecryptedValue());
		requestone.setHttpMethod('GET');
		requestone.setRequestHeader("Content-type", "application/json");
		var resp = requestone.execute();
		var nexmoreqbody = resp.getBody();
		if (nexmoreqbody) {
			nexmoreqbody.appID = gr.application_id;
			var nexmores = JSON.parse(nexmoreqbody);
			var obj = {};
			obj.appid = gr.application_id.toString();
			nexmores.push(obj);		
		    gs.info("nexmo-response ==>"+JSON.stringify(nexmores));
			return nexmores;
		}
	}

// 	var grIncident = new GlideRecord('notify_number');
// 	//grIncident.setLimit(1);
// 	grIncident.query();
// 	if (grIncident.next()) {
// 		gs.info("API key from config table ==> "+grIncident.number);
// 	}
// 	var nccotext = "test";
// 	response.setContentType('application/json');
// 	response.getStreamWriter().writeString(nccotext.toString());
// 	return response;
})(request, response);
